import axios from 'axios';
import React, { useEffect, useState } from 'react'
import Spinner from './Spinner'
import useGif from '../hooks/useGif';

const Tag = () => {
    // const API_KEY = process.env.REACT_APP_GIPHY_API_KEY;
    // const [gif,setGif] = useState('');
    // const [loading,setLoading] = useState(false);
    const [tag,setTag] = useState('');

    // async function fetchData() {
    //     setLoading(true);
    //     const url = `https://api.giphy.com/v1/gifs/random?api_key=${API_KEY}&tag=${tag}`;
    //   const {data} =   await axios.get(url);
    //   const imageSource = data.data.images.downsized_large.url;
    //   setGif(imageSource);
    //   setLoading(false);
    // }

    // useEffect(() =>{
    //     fetchData()
    // },[])
    const {gif,loading,fetchData} = useGif(tag);

  return (
    <div className='w-1/2 bg-blue-500 rounded-lg border border-black flex flex-col items-center
           gap-y-5 mt-[15px]  '>
        <h1 className='mt-[15px] text-2xl underline uppercase font-bold'> Random {tag} GIF</h1>
        {
            loading ? (<Spinner/>) : (<img src={gif} width={450}/>)
        }

        <input type='text' className='w-10/12 text-lg py-2 rounded-lg mb-[3px] text-center'
            onChange={(event) => {
                setTag(event.target.value)
            }}
            value={tag}
        />

        <button onClick={() => fetchData(tag)} className='w-10/12 bg-blue-200 text-lg py-2 rounded-lg mb-[20px]'>Generate</button>
    </div>
  )
}

export default Tag
