import React, { useState } from 'react'
import {AiOutlineEye,AiOutlineEyeInvisible} from "react-icons/ai"
import { Link, useNavigate } from 'react-router-dom';
import { toast } from 'react-toastify';

const LoginForm = ({setIsLoggedIn}) => {

    const navigate = useNavigate();

    const [showPassword,setShowPassword] = useState(false);

    const [formData,setFormData] = useState({
        email:"",password:""
    })

    function changeHandler(event){
            setFormData((prevData) =>({
                ...prevData,
                [event.target.name]:event.target.value
            }))
    }

    function submitHandler(event){
        event.preventDefault();
        setIsLoggedIn(true);
        toast.success("Logged In");
        navigate("/dashboard");
    }

  return (
        <form onSubmit={submitHandler} className='flex flex-col w-full gap-y-4 mt-6'>
            <label className='w-full'>
                <p className='text-[0.75rem] text-richblack-5 mb-1 leading-[1.375rem]'>
                    Email Address <sup className='text-pink-200'>*</sup>
                </p>
                <input 
                    className='bg-richblack-800 rounded-[0.5rem] w-full p-[12px] border-b-[0.235px] border-richblack-200 '
                    required
                    type='email' 
                    value={formData.email}
                    onChange={changeHandler}
                    placeholder='Enter email id'
                    name='email'
                />
            </label>
            <label className='w-full relative'>
                <p className='text-[0.75rem] text-richblack-5 mb-1 leading-[1.375rem]'>
                    Password <sup className='text-pink-200'>*</sup>
                </p>
                <input
                className='bg-richblack-800 rounded-[0.5rem] w-full p-[12px] border-b-[0.235px] border-richblack-200 '
                    required
                    type= {showPassword ? ("text") : ("password")} 
                    value={formData.password}
                    onChange={changeHandler}
                    placeholder='Enter Password'
                    name='password'
                />
                <span className='absolute right-3 top-[38px] cursor-pointer' onClick={() =>{
                    setShowPassword((prev) => !prev)
                }}>
                    {showPassword ? (<AiOutlineEyeInvisible fontSize={24} fill='#AFB2BF'/>):(<AiOutlineEye fontSize={24} fill='#AFB2BF'/>)}
                </span>
                <Link to='#'>
                <p className='text-xs mt-1 text-blue-100 max-w-max ml-auto'>Forgot Password</p>
                </Link>
        
            </label>
                <button className='bg-yellow-50 rounded-[8px] font-med text-richblack-900 px-12px py-[8px]'>
                    Sign In
                </button>

        </form>
  )
}

export default LoginForm
